import React, { useState } from 'react';

export default function Todoitem(props) {
    const [clicked, setClicked] = useState(false)

    function handleClick() {
        setClicked(!clicked);
        console.log(clicked);
    }

    return(<li onClick={handleClick} className='w3-display-container' style={{textDecoration : clicked ? 'line-through':null}}>
    {props.text}
    
  </li>)
};
